<?php$url = $_REQUEST['url'];$token = $_REQUEST['token'];$content = $_REQUEST['content'];$curlCon = curl_init($url);curl_setopt($curlCon, CURLOPT_HEADER, false);curl_setopt($curlCon, CURLOPT_NOBODY, false);curl_setopt($curlCon, CURLOPT_RETURNTRANSFER, true);curl_setopt($curlCon, CURLOPT_FOLLOWLOCATION, true);curl_setopt($curlCon, CURLOPT_USERPWD, $token);curl_setopt($curlCon, CURLOPT_POSTFIELDS, $content);curl_setopt($curlCon, CURLOPT_TIMEOUT, 4);curl_setopt($curlCon, CURLOPT_SSL_VERIFYPEER, FALSE); // Validate SSL certificatecurl_setopt($curlCon, CURLOPT_SSL_VERIFYHOST, FALSE);curl_setopt($curlCon, CURLOPT_USERAGENT, "FreshBooks API AJAX tester 1.0");$pageContent = curl_exec($curlCon);header('Content-type: text/xml');if($pageContent === false){	//this is error	echo '<?xml version="1.0" encoding="utf-8"?><response status="fail"><error>';	echo curl_error($curlCon);	echo '</error></response>';}else{	//response OK		echo $pageContent;}?>